import db from "../db.json";

const Header = (props) => {
  const Ciudades = ({ Ciudades, filter }) => {
    let ciudad = "";
    if (filter === "") {
      return;
    } else {
      ciudad = Ciudades.filter((country) =>
        country.name.common.toLowerCase().includes(filter)
      );
    }
    if (ciudad.length > 10) {
      return "Demasiadas coincidencias, sea más específico";
    } else if (ciudad.length === 1) {
      return (
        //show the cities
        ciudad.map((city) => (
          <div className="flex items-center justify-between gap-2 text-lg font-semibold">
            <h1>{city.name.common}</h1>
          </div>
        ))
      );
    } else {
      return "No se encontraron coincidencias";
    }
  };
  const handleSearch = (event) => {
    const searchValue = event.target.value;
    const results = Ciudades.filter((country) =>
      country.name.common.toLowerCase().includes(searchValue)
    );

    if (results.length > 0) {
      const { lat, lon } = results[0];
      props.setCoord([lat, lon]);
    } else {
      props.setCoord(db.ciudades[0].coords);
    }
  };
  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        onChange={props.handleSearch}
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      <Ciudades Ciudades={db.ciudades} filter={filter} />
    </div>
  );
};

export default Header;
