import { Humidity, Wind, Location } from "../Images/svg";

const Alerta = ({ weatherData }) => {
  //console.log(weatherData);
  const data = weatherData[0].alerts.alert[0];
  //console.log(data);

  // Divide la cadena original en un arreglo de elementos
  const items = data.desc.split("\n");

  return (
    <aside
      className={`flex-1 flex flex-col items-center justify-center m-auto w-1/3 ${weatherData[1] ? "bg-orange-300 border-l-4 border-orange-600 text-orange-700 p-4" : "bg-orange-100 border-l-4 border-orange-500 text-orange-700 p-4"} `}
    >
      <div className="flex flex-col items-center gap-4">
        <div className="flex items-center">
          <div className="text-xl font-bold transition-colors">
            {data.event}
          </div>
        </div>
        <div className="text-sm font-medium transition-colors">
          <ul className="list-disc list-inside">
            {items.map((key, index) => (
              <li key={index}>{key.slice(1)}</li>
            ))}
          </ul>
        </div>
      </div>
    </aside>
  );
};
export default Alerta;
