const Forecast = (weatherData) => {
  const data = weatherData.weatherData;

  // Objeto para almacenar los datos agrupados por día
  const groupedData = {};

  // Recorrer cada entrada del array
  data.list.forEach((entry) => {
    // Extraer solo la parte de la fecha (ignorar la hora)
    const date = entry.dt_txt.split(" ")[0];

    // Si la fecha no existe en groupedData, crear una nueva entrada con un array vacío
    if (!groupedData[date]) {
      groupedData[date] = [];
    }

    // Añadir la entrada al array correspondiente en groupedData
    groupedData[date].push(entry);
  });
  return (
    <aside className="flex-1 flex flex-col items-center justify-center px-4 md:px-6">
      {groupedData.forEach((day) => {
        return (
          <div className="flex flex-col items-center justify-center">
            <div className="text-2xl font-bold">{day.date}</div>
            <div className="flex flex-col items-center justify-center">
              {day.list.map((entry) => {
                return (
                  <div className="flex flex-col items-center justify-center">
                    <div className="text-lg font-bold">{entry.weather[0].main}</div>
                    <div className="text-sm">{entry.weather[0].description}</div>
                  </div>
                );
              })}
            </div>
          </div>
        );
      })}
    </aside>
  );
};
export default Forecast;
