import { useState } from "react";

export default function Component() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [forecast, setForecast] = useState([
    {
      day: "Monday",
      icon: "Sun",
      temp: 72,
    },
    {
      day: "Tuesday",
      icon: "Cloud",
      temp: 68,
    },
    {
      day: "Wednesday",
      icon: "Rain",
      temp: 64,
    },
    {
      day: "Thursday",
      icon: "Sun",
      temp: 70,
    },
    {
      day: "Friday",
      icon: "Cloud",
      temp: 66,
    },
  ]);
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };
  return (
    <div
      className={`flex flex-col w-screen h-screen transition-colors ${
        isDarkMode ? "bg-gray-900 text-white" : "bg-white text-gray-900"
      }`}
    >
      <header className="flex justify-between items-center h-16 px-4 border-b shrink-0 md:px-6">
        <div className="flex items-center gap-4">
          <link
            href="#"
            className="flex items-center gap-2 text-lg font-semibold md:text-base"
            prefetch={false}
          >
            <SunIcon className="w-6 h-6" />
            <span className="sr-only">Weather App</span>
          </link>
          <div className="w-full max-w-md">
            <Input
              type="search"
              placeholder="Search for a city..."
              value={searchTerm}
              onChange={handleSearch}
              className="w-full"
            />
          </div>
        </div>
        <div className="mt-8">
          <Toggle
            checked={isDarkMode}
            onCheckedChange={setIsDarkMode}
            aria-label="Toggle dark mode"
            className="bg-gray-200 dark:bg-gray-700 border-none"
          >
            <div
              className={`w-10 h-5 rounded-full transition-all ${
                isDarkMode
                  ? "bg-gray-900 justify-end"
                  : "bg-white justify-start"
              }`}
            >
              <div className="w-5 h-5 bg-white dark:bg-gray-900 rounded-full shadow" />
            </div>
          </Toggle>
        </div>
      </header>
      <main className="flex-1 flex flex-col items-center justify-center px-4 md:px-6">
        <div className="flex flex-col items-center gap-4">
          <div className="flex items-center gap-4">
            <SunIcon className="w-16 h-16" />
            <div className="text-6xl font-bold">72°</div>
          </div>
          <div className="text-2xl font-medium">Sunny</div>
          <div className="text-gray-500 dark:text-gray-400">
            San Francisco, CA
          </div>
        </div>
        <div className="mt-8 grid grid-cols-5 gap-4">
          {forecast.map((day, index) => (
            <div key={index} className="flex flex-col items-center gap-2">
              <div className="text-sm font-medium">{day.day}</div>
              <Volume1Icon className="w-8 h-8" />
              <div className="text-lg font-bold">{day.temp}°</div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

function CloudIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M17.5 19H9a7 7 0 1 1 6.71-9h1.79a4.5 4.5 0 1 1 0 9Z" />
    </svg>
  );
}

function CloudRainIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M4 14.899A7 7 0 1 1 15.71 8h1.79a4.5 4.5 0 0 1 2.5 8.242" />
      <path d="M16 14v6" />
      <path d="M8 14v6" />
      <path d="M12 16v6" />
    </svg>
  );
}

function SunIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <circle cx="12" cy="12" r="4" />
      <path d="M12 2v2" />
      <path d="M12 20v2" />
      <path d="m4.93 4.93 1.41 1.41" />
      <path d="m17.66 17.66 1.41 1.41" />
      <path d="M2 12h2" />
      <path d="M20 12h2" />
      <path d="m6.34 17.66-1.41 1.41" />
      <path d="m19.07 4.93-1.41 1.41" />
    </svg>
  );
}

function Volume1Icon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <polygon points="11 5 6 9 2 9 2 15 6 15 11 19 11 5" />
      <path d="M15.54 8.46a5 5 0 0 1 0 7.07" />
    </svg>
  );
}
