import { useState, useEffect } from "react";
import db from "../db.json";

const Search = (props) => {
  const [filter, setFilter] = useState("");
  const [cities, setCities] = useState(db.ciudades);

  console.log(cities);

  const handleSearch = (event) => {
    setFilter(event.target.value);
  };

  const Ciudades = ({ Ciudades, filter }) => {
    const results = cities.filter((city) =>
      city.nombre.toLowerCase().includes(filter.toLowerCase())
    );
    return (
      <div className="flex flex-col items-center justify-center gap-2 w-full">
        {results.map((city) => (
          <div className="flex items-center justify-between gap-2 text-lg font-semibold">
            <h1>{city.nombre}</h1>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        onChange={handleSearch}
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      <Ciudades Ciudades={cities} filter={filter} />
    </div>
  );
};

export default Search;
