import { Location } from "../Images/svg";
import { useState, useEffect } from "react";
import { fetchWeatherData } from "../Services/Weather";

const Main = ({ weatherData }) => {
  //console.log(weatherData);
  const { currentData, setCurrentData } = useState([]);
  const { loaded, setLoaded } = useState(false);
  const coords = weatherData[0];
  const units = weatherData[1];
  const isDarkMode = weatherData[2];

  console.log(coords);

  useEffect(() => {
    fetchWeatherData(coords[0], coords[1])
      .then((data) => {
        setCurrentData(data);
        setLoaded(true);
      })
      .catch((error) => console.error(error));
  }, []);

  const data = currentData;

  console.log(data);

  return loaded ? (
    <main
      className="flex-1 flex flex-col items-center justify-center m-auto p-4"
      key={data.location.name}
    >
      <div className="flex flex-col items-center gap-3">
        <div className="flex items-center gap-2">
          <img
            src={data.current.condition.icon}
            alt={data.current.condition.text}
          ></img>
          <div className="text-6xl font-bold transition-colors">
            {units === "c" ? data.current.temp_c : data.current.temp_f}Â°
          </div>
        </div>
        <div className="text-2xl font-medium transition-colors">
          {data.current.condition.text}
        </div>
        <div className="flex flex-1 font-thin transition-colors">
          <Location prop={isDarkMode} /> {data.location.name}
        </div>
      </div>
    </main>
  ) : null;
};
export default Main;
