import { useState, useEffect } from "react";
import db from "../db.json";

const Header = (props) => {
  const [filter, setFilter] = useState("");
  const [cities, setCities] = useState([]);

  useEffect(() => {
    const Ciudades = db.ciudades.filter((city) =>
      city.nombre.common.toLowerCase().includes(filter)
    );
    setCities(Ciudades);
    console.log(cities);
  });

  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      <Ciudades Ciudades={db.ciudades} filter={filter} />
    </div>
  );
};

export default Header;
