const Forecast = (weatherData) => {
  const data = weatherData.weatherData;
  const grupedData = Object.groupBy(data, ({ dt_txt }) => dt_txt);
  console.log(grupedData);
  return (
    <aside className="flex-1 flex flex-row items-center justify-center px-4 md:px-6 overflow-x-auto w-full">
      <div className="mt-8 flex flex-auto gap-4">
        {Object.entries(grupedData).map(([key, value]) => (
          <div
            key={key}
            className="flex flex-col items-center gap-2 m-auto border-red-500"
          >
            <div className="text-sm font-medium">{key}</div>
            <image src={`https://openweathermap.org/img/wn/${value[0].weather[0].icon}@2x.png`} className="h-10 w-10" />
            <div className="text-lg font-bold">{value[0].main.temp}Â°</div>
            <div className="text-sm font-medium">{value[0].weather[0].description}</div>
          </div>
        ))}
      </div>
    </aside>
  );
};
export default Forecast;
