import { useState } from "react";
import db from "../db.json";

const Header = (props) => {
  const [filter, setFilter] = useState("");

  const Ciudades = ({ Ciudades, filter }) => {
    console.log(Ciudades.includes(filter));
    let ciudad = "";
    if (filter === "") {
      return;
    } else {
      ciudad = Ciudades.filter((country) =>
        country.name.common.toLowerCase().includes(filter)
      );
    }
    console.log(ciudad);
  };
  const handleSearch = (event) => {
    setFilter(event.target.value);
  };
  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        onChange={handleSearch}
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      <Ciudades Ciudades={db.ciudades} filter={filter} />
    </div>
  );
};

export default Header;
