import { Humidity, Wind, Location } from "../Images/svg";

const Alerta = ({ weatherData }) => {
  //console.log(weatherData);
  const data = weatherData[0].alerts.alert[0];
  console.log(data);
  
  // Divide la cadena original en un arreglo de elementos
  const items = data.desc.split("\n");

  // Une los elementos con saltos de línea
  const formattedText = items.join("\n");

  return (
    <aside className="flex-1 flex flex-col items-center justify-center m-auto w-1/3">
      <div className="flex flex-col items-center gap-4">
        <div className="flex items-center">
          <div className="text-2xl font-bold transition-colors">
            {data.event}
          </div>
        </div>
        <div className="text-sm font-medium transition-colors">
          {formattedText}
        </div>
      </div>
    </aside>
  );
};
export default Alerta;
