import useState from "react";

const Header = (props) => {
  const Countries = ({ countries, filter, handleShow }) => {
    let country = "";
    if (filter === "") {
      return;
    } else {
      country = countries.filter((country) =>
        country.name.common.toLowerCase().includes(filter)
      );
    }
    if (country.length > 10) {
      return "To many matches, specify another filter";
    } else {
      return (
        <div className="w-full max-w-md">
          <input
            type="search"
            placeholder="Buscar ciudad..."
            onChange={props.handleSearch}
            className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
              props.isDarkMode
                ? "bg-gray-700 text-white"
                : "bg-gray-200 text-gray-700"
            }`}
          />
        </div>
      );
    }
  };
};

export default Header;
