import { useState, useEffect } from "react";
import db from "../db.json";

const Search = (props) => {
  const [filter, setFilter] = useState("");
  const [cities, setCities] = useState(db.ciudades);

  console.log(cities);

  const handleSearch = (event) => {
    const searchValue = event.target.value;
    const results = cities.filter((city) =>
      city.name.common.toLowerCase().includes(searchValue)
    );
    
    console.log(results);
  };
  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        onChange={handleSearch}
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      {/* <Ciudades Ciudades={db.ciudades} filter={filter} /> */}
    </div>
  );
};

export default Search;
