import React from "react";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
} from "recharts";
import { Rain } from "../Images/svg";

const ForecastHora = ({ weatherData }) => {
  const date = new Date();
  let hours = date.getHours() + 1;

  const forecast = [
    ...weatherData[0].forecast.forecastday[0].hour,
    ...weatherData[0].forecast.forecastday[1].hour,
    ...weatherData[0].forecast.forecastday[2].hour,
  ];

  // Formatear los datos para el gráfico
  const data = forecast.slice(hours, 24 + hours).map((hour) => ({
    time:
      hour.time.slice(11, 13) > 12
        ? `${hour.time.slice(11, 13) - 12} p.m.`
        : `${hour.time.slice(11, 13)} a.m.`,
    text: hour.condition.text,
    temp: weatherData[1] === "c" ? hour.temp_c : hour.temp_f,
    chance_of_rain: hour.chance_of_rain,
    icon: hour.condition.icon,
  }));

  return (
    <aside className="flex-1 flex flex-col items-center justify-center overflow-x-auto w-[90%] sm:w-[90%] m-auto no-scrollbar">
      <div className="mt-4 flex flex-auto gap-2 w-full">
        <LineChart data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="time" />
          <YAxis />
          <Tooltip />
          <Line
            type="monotone"
            dataKey="temp"
            stroke="#8884d8"
            activeDot={{ r: 8 }}
          />
        </LineChart>
      </div>
      <div className="mt-4 flex flex-auto gap-2 w-full">
        {data.map((hour) => (
          <div
            key={hour.time}
            id={hour.time}
            className="flex flex-col items-center justify-center gap-2 mt-5 mb-5 min-w-24 min-h-60"
          >
            <div className="text-sm font-medium transition-colors">
              {hour.time}
            </div>
            <img src={hour.icon} alt={hour.text}></img>
            <div className="text-lg font-bold">{hour.temp}°</div>
            <div className="text-sm font-normal text-center min-h-16 transition-colors">
              {hour.text}
            </div>
            <div className="flex flex-1 text-sm font-normal text-center transition-colors">
              <Rain prop={weatherData[2]} /> {hour.chance_of_rain} %
            </div>
          </div>
        ))}
      </div>
    </aside>
  );
};

export default ForecastHora;
