import { useState } from "react";
import db from "../db.json";

const Header = (props) => {
  const [filter, setFilter] = useState("");

  const Ciudades = ({ Ciudades, filter }) => {
    console.log(Ciudades.filter((ciudad) => (
      console.log(ciudad.name.common.toLowerCase().includes(filter.toLowerCase()))
    )));
  };
    // Función para buscar una ciudad por nombre
    const findCityByName = (nombreCiudad) => {
      return db.ciudades.filter(
        (ciudad) => ciudad.nombre.toLowerCase() === nombreCiudad.toLowerCase()
      );
    };
  
  // Manejar la búsqueda de ciudades
  const handleSearch = (e) => {
    const searchValue = e.target.value;
    const results = findCityByName(searchValue);

    if (results.length > 0) {
      const { lat, lon } = results[0];
      props.setCoords([lat, lon]);
    } else {
      props.setCoords(props.originalCoords);
    }
  };

  return (
    <div className="w-full max-w-md">
      <input
        type="search"
        placeholder="Buscar ciudad..."
        onChange={handleSearch}
        className={`w-full h-full rounded-md p-2 transition-colors bg-gray-200 dark:bg-gray-800 ${
          props.isDarkMode
            ? "bg-gray-700 text-white"
            : "bg-gray-200 text-gray-700"
        }`}
      />
      <Ciudades Ciudades={db.ciudades} filter={filter} />
    </div>
  );
};

export default Header;
