// Función para agrupar los datos por fecha
const groupBy = (array, keyFunc) => {
  return array.reduce((result, item) => {
    const key = keyFunc(item);
    if (!result[key]) {
      result[key] = [];
    }
    result[key].push(item);
    return result;
  }, {});
};

const Forecast = ({ weatherData }) => {
  // Agrupando los datos por fecha
  const groupedData = groupBy(weatherData, (item) => item.dt_txt);

  return (
    <div className="mt-8 grid grid-cols-5 gap-4">
      <aside className="flex-1 flex flex-col items-center justify-center px-4 md:px-6">
        {Object.entries(groupedData).map(([key, value]) => (
          <div key={key}>
            <h3>{key}</h3>
            {value.map((weather, index) => (
              <div key={index}>
                <p>{weather.main.temp} °C</p>
                <p>{weather.weather[0].description}</p>
              </div>
            ))}
          </div>
        ))}
      </aside>
    </div>
  );
};

export default Forecast;
