const Forecast = (weatherData) => {
  const data = weatherData.weatherData.slice(0, 11);
  const grupedData = Object.groupBy(data, ({ dt_txt }) => dt_txt);
  console.log(grupedData);
  return (
    <aside className="flex-1 flex flex-row items-center justify-center px-4 md:px-6 overflow-x-auto w-[80%] m-auto">
      <div className="mt-8 flex flex-auto gap-4">
        {Object.entries(grupedData).map(([key, value]) => (
          <div key={key} className="flex flex-col items-center gap-2 m-0">
            <div className="text-sm font-medium">
              {key.slice(10, 13) > 12
                ? `${key.slice(10, 13) - 12} PM`
                : `${key.slice(10, 13)} AM`}
            </div>
            <img
              src={`https://openweathermap.org/img/wn/${value[0].weather[0].icon}@2x.png`}
              alt={value[0].weather[0].description}
            ></img>
            <div className="text-lg font-bold">{value[0].main.temp}Â°</div>
            <div className="text-sm font-medium">
              {value[0].weather[0].description}
            </div>
          </div>
        ))}
      </div>
    </aside>
  );
};
export default Forecast;
