import { useState, useEffect } from "react";
import { Route, Routes, Link } from "react-router-dom";
import Home from "./pages/Home";
import Forecast from "./pages/Forecast";

export default function Component() {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [forecast, setForecast] = useState([
    {
      day: "Monday",
      icon: "Sun",
      temp: 72,
    },
    {
      day: "Tuesday",
      icon: "Cloud",
      temp: 68,
    },
    {
      day: "Wednesday",
      icon: "Rain",
      temp: 64,
    },
    {
      day: "Thursday",
      icon: "Sun",
      temp: 70,
    },
    {
      day: "Friday",
      icon: "Cloud",
      temp: 66,
    },
  ]);

  const [imgUrl, setImgUrl] = useState("");
  const [loaded, setLoaded] = useState(false);
  const [coords, setCoords] = useState([]);

  function ubicacion(posision) {
    const coordenadas = posision.coords;
    setCoords([coordenadas.latitude, coordenadas.longitude]);
  }
  navigator.geolocation.getCurrentPosition(ubicacion);
  useEffect(() => {
    setLoaded(false);
    const getWeather = async () => {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/forecast?lat=${coords[0]}&lon=${coords[1]}&appid=${process.env.REACT_APP_API_KEY}&units=metric`
      );
      const data = await response.json();
      console.log(data);
      setLoaded(true);
    };

    getWeather();
  }, []);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
  };
  return (
    <div
      className={`flex flex-col w-screen h-screen transition-colors ${
        isDarkMode ? "bg-gray-900 text-white" : "bg-white text-gray-900"
      }`}
    >
      <header className="flex items-center h-16 px-4 border-b shrink-0 md:px-6">
        <div className="flex items-center gap-4">
          <Link
            href="#"
            className="flex items-center justify-between gap-2 text-lg font-semibold md:text-base"
            prefetch={false}
          >
            <SunIcon className="w-6 h-6" />
            <span className="sr-only">Weather App</span>
          </Link>{" "}
          <div className="w-full max-w-md">
            <input
              type="search"
              placeholder="Buscar ciudad..."
              value={searchTerm}
              onChange={handleSearch}
              className={`w-full h-full rounded-md p-2 transition-colors ${
                isDarkMode ? "bg-gray-800 text-white" : "bg-white text-gray-900"
              }`}
            />
          </div>
        </div>
      </header>
      <main className="flex-1 flex flex-col items-center justify-center px-4 md:px-6">
        <div className="flex flex-col items-center gap-4">
          <div className="flex items-center gap-4">
            <SunIcon className="w-16 h-16" />
            <div className="text-6xl font-bold">72°</div>
          </div>
          <div className="text-2xl font-medium">Sunny</div>
          <div className="text-gray-500 dark:text-gray-400">
            San Francisco, CA
          </div>
        </div>
        <div className="mt-8 grid grid-cols-5 gap-4">
          {forecast.map((day, index) => (
            <div key={index} className="flex flex-col items-center gap-2">
              <div className="text-sm font-medium">{day.day}</div>
              <SunIcon className="w-8 h-8" />
              <div className="text-lg font-bold">{day.temp}°</div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}
function CloudIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M17.5 19H9a7 7 0 1 1 6.71-9h1.79a4.5 4.5 0 1 1 0 9Z" />
    </svg>
  );
}

function CloudRainIcon(props) {
  return (
    <svg
      {...props}
      xmlns="http://www.w3.org/2000/svg"
      width="24"
      height="24"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
    >
      <path d="M4 14.899A7 7 0 1 1 15.71 8h1.79a4.5 4.5 0 0 1 2.5 8.242" />
      <path d="M16 14v6" />
      <path d="M8 14v6" />
      <path d="M12 16v6" />
    </svg>
  );
}

